# -*- coding: utf-8 -*-
import os

from lxml import etree
import requests


def down_pic(pro, city):
    try:
        url = r'https://pixabay.com/zh/photos/' + city + '/?image_type=photo&orientation=horizontal'
        html = requests.get(url)
        html.encoding = 'utf-8'
        html = html.text
        temp = etree.HTML(html)
        result = temp.xpath('//*[@id="content"]/div/div[2]/div/div[2]/div[1]/a/img/@src')[0]
        pic = requests.get(result, timeout=15)
        string = pro + '/' + city + '.jpg'
        with open(string, 'wb') as f:
            f.write(pic.content)
        print(city + "," + pro + "," + city + ".jpg")
    except Exception as e:
        print(city + "," + pro + "," + pro + ".jpg")


